import org.junit.jupiter.params.ParameterizedTest
import org.junit.jupiter.params.provider.Arguments
import org.junit.jupiter.params.provider.MethodSource
import java.util.stream.Stream
import kotlin.test.assertEquals

class LongestPalindromicSubstringTest {

    @ParameterizedTest(name = "given a string {0}, then it should return {1}")
    @MethodSource("arguments")
    fun execute(
        s: String,
        expected: String
    ) {
        val actual = LongestPalindromicSubstring().longestPalindrome(s)
        assertEquals(expected, actual)
    }

    private companion object {
        val stringOfC = ""

        @JvmStatic
        fun arguments(): Stream<Arguments> = Stream.of(
            Arguments.of(stringOfC, stringOfC),
            Arguments.of("a", "a"),
            Arguments.of("aacabdkacaa", "aca"),
            Arguments.of("babad", "bab"),
            Arguments.of("cbbd", "bb"),
        )
    }
}